&lt;h4 id=&#34;å®Œæ•´ä»£ç -1&#34;&gt;&lt;a href=&#34;#å®Œæ•´ä»£ç -1&#34; class=&#34;headerlink&#34; title=&#34;å®Œæ•´ä»£ç &#34;&gt;&lt;/a&gt;å®Œæ•´ä»£ç &lt;/h4&gt;&lt;pre&gt;&lt;code&gt;class Solution &amp;#123;
public:
    bool lemonadeChange(vector&amp;lt;int&amp;gt;&amp;amp; bills) &amp;#123;
        int five=0,ten=0;
        for(int bill:bills)&amp;#123;
            if(bill==5)&amp;#123;
                five++;
            &amp;#125;
            else if(bill==10)&amp;#123;
                if(five!=0)&amp;#123;
                    five--;
                    ten++;
                &amp;#125;
                else&amp;#123;
                    return false;
                &amp;#125;
            &amp;#125;
            else if(bill==20)&amp;#123;
                if(ten&amp;gt;0 &amp;amp;&amp;amp; five&amp;gt;0)&amp;#123;
                    ten--;
                    five--;
                &amp;#125;
                else if(five&amp;gt;=3)&amp;#123;
                    five-=3;
                &amp;#125;
                else&amp;#123;
                    return false;
                &amp;#125;

            &amp;#125;
        &amp;#125;
        return true;
    &amp;#125;
&amp;#125;;
