&lt;h3 id=&#34;ACä»£ç -1&#34;&gt;&lt;a href=&#34;#ACä»£ç -1&#34; class=&#34;headerlink&#34; title=&#34;ACä»£ç &#34;&gt;&lt;/a&gt;ACä»£ç &lt;/h3&gt;&lt;pre&gt;&lt;code&gt;class Solution &amp;#123;
public:
    vector&amp;lt;int&amp;gt; plusOne(vector&amp;lt;int&amp;gt;&amp;amp; digits) &amp;#123;
        int len=digits.size();
        if(digits[len-1]!=9)&amp;#123;
            digits[len-1]+=1;
        &amp;#125;
        else&amp;#123;
            int cnt=0;//è®°å½•9å‡ºç°çš„æ¬¡æ•°ï¼Œç¬¬ä¸€ä¸ªé9å…ƒç´ çš„ä¸‹æ ‡å³ä¸ºlen-cnt-1
            for(int i=len-1;i&amp;gt;=0;i--)&amp;#123;
                if(digits[i]==9)&amp;#123;
                    cnt++;
                &amp;#125;
                else&amp;#123;
                    break;
                &amp;#125;
            &amp;#125;
            if(cnt==len)&amp;#123;
                digits.insert(digits.begin(),1);
                for(int i=1;i&amp;lt;len+1;i++)&amp;#123;
                    digits[i]=0;
                &amp;#125;
            &amp;#125;
            else&amp;#123;
                int index=len-cnt-1;
                digits[index]+=1;
                for(int i=index+1;i&amp;lt;len;i++)&amp;#123;
                    digits[i]=0;
                &amp;#125;
            &amp;#125;
        &amp;#125;
        return digits;
    &amp;#125;
&amp;#125;;
