                            sum+=img[r][c];
                            cnt++;
                        &amp;#125;
                    &amp;#125;
                &amp;#125;
                ans[i][j]=cnt&amp;gt;0?sum/cnt:img[i][j];
            &amp;#125;
        &amp;#125;
        return ans;
    &amp;#125;
