&lt;h2 id=&#34;å®Œæ•´ä»£ç -6&#34;&gt;&lt;a href=&#34;#å®Œæ•´ä»£ç -6&#34; class=&#34;headerlink&#34; title=&#34;å®Œæ•´ä»£ç &#34;&gt;&lt;/a&gt;å®Œæ•´ä»£ç &lt;/h2&gt;&lt;p&gt;æ„Ÿè°¢&lt;a href=&#34;https://leetcode.cn/u/endlesscheng/&#34;&gt;çµç¥&lt;/a&gt;é¢˜è§£ï¼Œæ•™ä¼šæˆ‘å¾ˆå¤š&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;language-cpp&#34;&gt;class Solution &amp;#123;
public:
    vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt; findMatrix(vector&amp;lt;int&amp;gt;&amp;amp; nums) &amp;#123;
        unordered_map&amp;lt;int,int&amp;gt; cnt;
        vector&amp;lt;vector&amp;lt;int&amp;gt;&amp;gt; ans;
        for(int x:nums)&amp;#123;
            cnt[x]++;
        &amp;#125;
        while(!cnt.empty())&amp;#123;
            vector&amp;lt;int&amp;gt;row;
            for(auto it=cnt.begin();it!=cnt.end();)&amp;#123;
                row.push_back(it-&amp;gt;first);
                if(--it-&amp;gt;second==0)&amp;#123;
                    it=cnt.erase(it);
                &amp;#125;
                else&amp;#123;
                    it++;
                &amp;#125;
            &amp;#125;
            ans.push_back(row);
        &amp;#125;
        return ans;
    &amp;#125;
&amp;#125;;
&lt;/code&gt;&lt;/pre&gt;
&lt;h1 id=&#34;2643-ä¸€æœ€å¤šçš„è¡Œ&#34;&gt;&lt;a href=&#34;#2643-ä¸€æœ€å¤šçš„è¡Œ&#34; class=&#34;headerlink&#34; title=&#34;2643.ä¸€æœ€å¤šçš„è¡Œ&#34;&gt;&lt;/a&gt;2643.ä¸€æœ€å¤šçš„è¡Œ&lt;/h1&gt;&lt;blockquote&gt;
